import React, { useState } from "react";
import ActionButtons from "./ActionButtons";
import Button from "./Button"; // Make sure to import Button here for the edit state

function Artist({
  artist,
  onSelectArtist,
  onDeleteArtist,
  onUpdateArtist,
  isSelected,
}) {
  const [isEditing, setIsEditing] = useState(false);
  const [newName, setNewName] = useState(artist.name);

  const handleUpdate = () => {
    onUpdateArtist(artist.name, newName);
    setIsEditing(false);
  };

  return (
    <li className={isSelected ? "selected" : ""}>
      {isEditing ? (
        <>
          <input
            className={"edit-text"}
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
          />
          <Button className={"btn-save"} onClick={handleUpdate}>
            üëçüèº
          </Button>
          <Button className={"btn-delete"} onClick={() => setIsEditing(false)}>
            üëéüèº
          </Button>
        </>
      ) : (
        <>
          <h3 onClick={() => onSelectArtist(artist)}>{artist.name}</h3>
          <ActionButtons
            onDelete={() => onDeleteArtist(artist.name)}
            onUpdate={() => setIsEditing(true)}
          />
        </>
      )}
    </li>
  );
}

export default Artist;
