import React, { useEffect, useRef, useState } from "react";
import Axios from "axios";

export default function App() {
  const [list, setList] = useState([]);
  const [selectedArtist, setSelectedArtist] = useState(null);
  const [selectedAlbum, setSelectedAlbum] = useState(null);

  useEffect(() => {
    Axios.get("http://localhost:4000/api/retrieveMusicLibrary").then((res) =>
      setList(res.data)
    );
  }, []);

  function handleSelectArtist(artist) {
    setSelectedArtist((selectedArtist) =>
      artist === selectedArtist ? null : artist
    );
    setSelectedAlbum(null); // Reset selected album when a new artist is selected
  }

  function handleSelectAlbum(album) {
    setSelectedAlbum((selectedAlbum) =>
      album === selectedAlbum ? null : album
    );
  }

  function handleCloseAlbum() {
    setSelectedAlbum(null);
  }

  async function handleDeleteArtist(artistName) {
    await Axios.delete(`http://localhost:4000/api/deleteArtist/${artistName}`);
    setList((list) => list.filter((artist) => artist.name !== artistName));
    setSelectedArtist(null);
    setSelectedAlbum(null);
  }

  async function handleDeleteAlbum(artistName, albumTitle) {
    await Axios.delete(
      `http://localhost:4000/api/deleteAlbum/${artistName}/${albumTitle}`
    );

    setList((list) =>
      list.map((artist) =>
        artist.name === artistName
          ? {
              ...artist,
              albums: artist.albums.filter(
                (album) => album.title !== albumTitle
              ),
            }
          : artist
      )
    );

    setSelectedAlbum(null);
    setSelectedArtist((prevSelectedArtist) => ({
      ...prevSelectedArtist,
      albums: prevSelectedArtist.albums.filter(
        (album) => album.title !== albumTitle
      ),
    }));
  }

  async function handleUpdateArtist(oldName, newName) {
    await Axios.put(`http://localhost:4000/api/updateArtist/`, {
      oldName,
      newName,
    });

    setList((list) =>
      list.map((artist) =>
        artist.name === oldName
          ? {
              ...artist,
              name: newName,
            }
          : artist
      )
    );

    if (selectedArtist && selectedArtist.name === oldName) {
      setSelectedArtist((prevSelectedArtist) => ({
        ...prevSelectedArtist,
        name: newName,
      }));
    }
  }

  async function handleUpdateAlbum(
    artistName,
    oldTitle,
    newTitle,
    newDescription
  ) {
    await Axios.put(`http://localhost:4000/api/updateAlbum/`, {
      artistName,
      oldTitle,
      newTitle,
      newDescription,
    });

    setList((list) =>
      list.map((artist) =>
        artist.name === artistName
          ? {
              ...artist,
              albums: artist.albums.map((album) =>
                album.title === oldTitle
                  ? { ...album, title: newTitle, description: newDescription }
                  : album
              ),
            }
          : artist
      )
    );

    if (selectedAlbum && selectedAlbum.title === oldTitle) {
      setSelectedAlbum((prevSelectedAlbum) => ({
        ...prevSelectedAlbum,
        title: newTitle,
        description: newDescription,
      }));
    }
  }

  async function handleAddArtist(artistName) {
    await Axios.post(`http://localhost:4000/api/addArtist/`, {
      name: artistName,
    });
    setList([...list, { name: artistName, albums: [] }]);
  }

  async function handleAddAlbum(artistName, albumTitle, albumDescription) {
    await Axios.post(`http://localhost:4000/api/addAlbum/`, {
      artistName,
      title: albumTitle,
      description: albumDescription,
    });

    setList((list) =>
      list.map((artist) =>
        artist.name === artistName
          ? {
              ...artist,
              albums: [
                ...artist.albums,
                { title: albumTitle, description: albumDescription, songs: [] },
              ],
            }
          : artist
      )
    );
  }

  async function handleAddSong(artistName, albumTitle, songTitle, songLength) {
    await Axios.post(`http://localhost:4000/api/addSong/`, {
      artistName,
      albumTitle,
      title: songTitle,
      length: songLength,
    });

    setList((list) =>
      list.map((artist) =>
        artist.name === artistName
          ? {
              ...artist,
              albums: artist.albums.map((album) =>
                album.title === albumTitle
                  ? {
                      ...album,
                      songs: [
                        ...album.songs,
                        { title: songTitle, length: songLength },
                      ],
                    }
                  : album
              ),
            }
          : artist
      )
    );
  }

  return (
    <>
      <NavBar>
        <Search />
      </NavBar>

      <Main>
        <Box title="Artists 🎤" onAdd={handleAddArtist} addType="artist">
          <ArtistList
            list={list}
            onSelectArtist={handleSelectArtist}
            onDeleteArtist={handleDeleteArtist}
            onUpdateArtist={handleUpdateArtist}
            selectedArtist={selectedArtist}
          />
        </Box>

        <Box
          title="Albums 💿"
          onAdd={(albumData) =>
            handleAddAlbum(
              selectedArtist.name,
              albumData.title,
              albumData.description
            )
          }
          addType="album"
          selectedArtist={selectedArtist}
        >
          {selectedArtist ? (
            <AlbumList
              artist={selectedArtist}
              onSelectAlbum={handleSelectAlbum}
              onDeleteAlbum={handleDeleteAlbum}
              onUpdateAlbum={handleUpdateAlbum}
              selectedAlbum={selectedAlbum}
            />
          ) : (
            <div className="center-text-container">
              <p>Select an artist first in order to see the albums. 👈🏻⚠️</p>
            </div>
          )}
        </Box>

        <Box
          title="Details 📃"
          onAdd={(songData) =>
            handleAddSong(
              selectedArtist.name,
              selectedAlbum.title,
              songData.title,
              songData.length
            )
          }
          addType="song"
          selectedAlbum={selectedAlbum}
        >
          {selectedAlbum ? (
            <AlbumDetails
              album={selectedAlbum}
              onCloseAlbum={handleCloseAlbum}
            />
          ) : (
            <div className="center-text-container">
              <p>Select an album first in order to see the details. 👈🏻⚠️</p>
            </div>
          )}
        </Box>
      </Main>
    </>
  );
}

function NavBar({ children }) {
  return (
    <nav className="nav-bar">
      <Logo />
      {children}
    </nav>
  );
}

function Logo() {
  return (
    <div className="logo">
      <span role="img">🎶</span>
      <h1>Music Library</h1>
    </div>
  );
}

function Search() {
  const inputEl = useRef(null);

  return (
    <input
      className="search"
      type="text"
      placeholder="Search albums..."
      ref={inputEl}
    />
  );
}

function Main({ children }) {
  return <main className="main">{children}</main>;
}

function Box({
  title,
  children,
  onAdd,
  addType,
  selectedArtist,
  selectedAlbum,
}) {
  const [isOpen, setIsOpen] = useState(true);
  const [isAdding, setIsAdding] = useState(false);

  const handleAdd = (event) => {
    event.preventDefault();
    const formData = new FormData(event.target);
    const data = Object.fromEntries(formData.entries());

    if (addType === "artist") {
      onAdd(data.name);
    } else if (addType === "album") {
      onAdd(data);
    } else if (addType === "song") {
      onAdd(data);
    }

    setIsAdding(false);
  };

  return (
    <div className="box">
      <button className="btn-toggle" onClick={() => setIsOpen((open) => !open)}>
        {isOpen ? "-" : "+"}
      </button>
      <h1 className={"box-title"}>{title}</h1>
      {isOpen && (
        <>
          {isAdding ? (
            <form onSubmit={handleAdd}>
              {addType === "artist" && (
                <>
                  <input
                    name="name"
                    type="text"
                    placeholder="Artist Name"
                    required
                  />
                  <button type="submit">Add Artist</button>
                </>
              )}
              {addType === "album" && selectedArtist && (
                <>
                  <input
                    name="title"
                    type="text"
                    placeholder="Album Title"
                    required
                  />
                  <textarea
                    name="description"
                    placeholder="Album Description"
                    required
                  ></textarea>
                  <button type="submit">Add Album</button>
                </>
              )}
              {addType === "song" && selectedAlbum && (
                <>
                  <input
                    name="title"
                    type="text"
                    placeholder="Song Title"
                    required
                  />
                  <input
                    name="length"
                    type="text"
                    placeholder="Song Length"
                    required
                  />
                  <button type="submit">Add Song</button>
                </>
              )}
              <button type="button" onClick={() => setIsAdding(false)}>
                Cancel
              </button>
            </form>
          ) : (
            <button onClick={() => setIsAdding(true)}>
              Add New {title.slice(0, -1)}
            </button>
          )}
          {children}
        </>
      )}
    </div>
  );
}

function ArtistList({
  list,
  onSelectArtist,
  onDeleteArtist,
  onUpdateArtist,
  selectedArtist,
}) {
  if (list.length === 0) {
    return (
      <div className="center-text-container">
        <p>There are no artists in the database yet. Start adding some! 🎤</p>
      </div>
    );
  }

  return (
    <ul>
      {list.map((artist) => (
        <Artist
          artist={artist}
          key={artist.name}
          onSelectArtist={onSelectArtist}
          onDeleteArtist={onDeleteArtist}
          onUpdateArtist={onUpdateArtist}
          isSelected={artist === selectedArtist}
        />
      ))}
    </ul>
  );
}

function Artist({
  artist,
  onSelectArtist,
  onDeleteArtist,
  onUpdateArtist,
  isSelected,
}) {
  const [isEditing, setIsEditing] = useState(false);
  const [newName, setNewName] = useState(artist.name);

  const handleUpdate = () => {
    onUpdateArtist(artist.name, newName);
    setIsEditing(false);
  };

  return (
    <li className={isSelected ? "selected" : ""}>
      {isEditing ? (
        <>
          <input value={newName} onChange={(e) => setNewName(e.target.value)} />
          <button onClick={handleUpdate}>Save</button>
          <button onClick={() => setIsEditing(false)}>Cancel</button>
        </>
      ) : (
        <>
          <span onClick={() => onSelectArtist(artist)}>{artist.name}</span>
          <button onClick={() => setIsEditing(true)}>Edit</button>
          <button onClick={() => onDeleteArtist(artist.name)}>Delete</button>
        </>
      )}
    </li>
  );
}

function AlbumList({
  artist,
  onSelectAlbum,
  onDeleteAlbum,
  onUpdateAlbum,
  selectedAlbum,
}) {
  if (artist.albums.length === 0) {
    return (
      <div className="center-text-container">
        <p>No albums found for this artist. Start adding some! 💿</p>
      </div>
    );
  }

  return (
    <ul>
      {artist.albums.map((album) => (
        <Album
          album={album}
          key={album.title}
          artistName={artist.name}
          onSelectAlbum={onSelectAlbum}
          onDeleteAlbum={onDeleteAlbum}
          onUpdateAlbum={onUpdateAlbum}
          isSelected={album === selectedAlbum}
        />
      ))}
    </ul>
  );
}

function Album({
  album,
  artistName,
  onSelectAlbum,
  onDeleteAlbum,
  onUpdateAlbum,
  isSelected,
}) {
  const [isEditing, setIsEditing] = useState(false);
  const [newTitle, setNewTitle] = useState(album.title);
  const [newDescription, setNewDescription] = useState(album.description);

  const handleUpdate = () => {
    onUpdateAlbum(artistName, album.title, newTitle, newDescription);
    setIsEditing(false);
  };

  return (
    <li className={isSelected ? "selected" : ""}>
      {isEditing ? (
        <>
          <input
            value={newTitle}
            onChange={(e) => setNewTitle(e.target.value)}
          />
          <textarea
            value={newDescription}
            onChange={(e) => setNewDescription(e.target.value)}
          />
          <button onClick={handleUpdate}>Save</button>
          <button onClick={() => setIsEditing(false)}>Cancel</button>
        </>
      ) : (
        <>
          <span onClick={() => onSelectAlbum(album)}>{album.title}</span>
          <button onClick={() => setIsEditing(true)}>Edit</button>
          <button onClick={() => onDeleteAlbum(artistName, album.title)}>
            Delete
          </button>
        </>
      )}
    </li>
  );
}

function AlbumDetails({ album, onCloseAlbum }) {
  if (!album) return null;

  return (
    <div className="album-details">
      <button className="btn-close" onClick={onCloseAlbum}>
        &times;
      </button>
      <h2>{album.title}</h2>
      <p>{album.description}</p>
      <SongList songs={album.songs} />
    </div>
  );
}

function SongList({ songs }) {
  if (songs.length === 0) {
    return (
      <div className="center-text-container">
        <p>No songs found for this album. Start adding some! 🎶</p>
      </div>
    );
  }

  return (
    <ul>
      {songs.map((song, index) => (
        <li key={index}>
          {song.title} - {song.length}
        </li>
      ))}
    </ul>
  );
}
