import React, { useState } from "react";

function Artist({
  artist,
  onSelectArtist,
  onDeleteArtist,
  onUpdateArtist,
  isSelected,
}) {
  const [isEditing, setIsEditing] = useState(false);
  const [newName, setNewName] = useState(artist.name);

  const handleUpdate = () => {
    onUpdateArtist(artist.name, newName);
    setIsEditing(false);
  };

  return (
    <li className={isSelected ? "selected" : ""}>
      {isEditing ? (
        <>
          <input
            className={"edit-text"}
            value={newName}
            onChange={(e) => setNewName(e.target.value)}
          />
          <button className={"btn-save"} onClick={handleUpdate}>
            üëçüèº
          </button>
          <button className={"btn-delete"} onClick={() => setIsEditing(false)}>
            üëéüèº
          </button>
        </>
      ) : (
        <>
          <h3 onClick={() => onSelectArtist(artist)}>{artist.name}</h3>
          <button
            className={"btn-delete"}
            onClick={() => onDeleteArtist(artist.name)}
          >
            üóëÔ∏è
          </button>
          <button className={"btn-update"} onClick={() => setIsEditing(true)}>
            ‚úèÔ∏è
          </button>
        </>
      )}
    </li>
  );
}

function OperationsButtons(onDelete, setIsEditing) {
  return;
  <>
    <button className={"btn-delete"} onClick={() => onDelete(artist.name)}>
      üóëÔ∏è
    </button>
    <button className={"btn-update"} onClick={() => setIsEditing(true)}>
      ‚úèÔ∏è
    </button>
  </>;
}

export default Artist;
