import React from "react";
import AddForm from "./AddForm";

function Box({
  selectedArtist,
  selectedAlbum,
  list,
  onSelectArtist,
  onSelectAlbum,
  onDeleteArtist,
  onDeleteAlbum,
  onDeleteSong,
  onAddArtist,
  newArtistName,
  setNewArtistName,
  onAddAlbum,
  newAlbumName,
  setNewAlbumName,
  onAddSong,
  newSongName,
  newSongLength,
  setNewSongName,
  setNewSongLength,
  onUpdateArtist,
  onUpdateAlbum,
  onUpdateSong,
}) {
  return (
    <div>
      {selectedArtist && (
        <div>
          <h2>{selectedArtist.name}</h2>
          <button onClick={() => onDeleteArtist(selectedArtist.name)}>
            Delete Artist
          </button>
          <AddForm
            label="Add Album"
            newEntry={newAlbumName}
            setNewEntry={setNewAlbumName}
            onSubmit={onAddAlbum}
          />
        </div>
      )}

      {selectedAlbum && (
        <div>
          <h3>{selectedAlbum.title}</h3>
          <button
            onClick={() =>
              onDeleteAlbum(selectedArtist.name, selectedAlbum.title)
            }
          >
            Delete Album
          </button>
          <AddForm
            label="Add Song"
            newEntry={newSongName}
            setNewEntry={setNewSongName}
            newEntryLength={newSongLength}
            setNewEntryLength={setNewSongLength}
            onSubmit={onAddSong}
          />
        </div>
      )}

      {!selectedArtist && !selectedAlbum && (
        <AddForm
          label="Add Artist"
          newEntry={newArtistName}
          setNewEntry={setNewArtistName}
          onSubmit={onAddArtist}
        />
      )}
    </div>
  );
}

export default Box;
