import React from "react";
import Album from "./Album";

function AlbumList({
  artist,
  onSelectAlbum,
  onDeleteAlbum,
  onUpdateAlbum,
  selectedAlbum,
}) {
  return artist.albums && artist.albums.length > 0 ? (
    <ul className="list list-library">
      {artist.albums.map((album) => (
        <Album
          album={album}
          key={album.title}
          onSelectAlbum={onSelectAlbum}
          onDeleteAlbum={onDeleteAlbum}
          onUpdateAlbum={onUpdateAlbum}
          artistName={artist.name}
          isSelected={selectedAlbum && selectedAlbum.title === album.title}
        />
      ))}
    </ul>
  ) : (
    <div className="center-text-container">
      <p className="box-text-no-data">
        No data available.<br></br>Start by adding a song to the album or add a
        description by editing the album. ‚úèÔ∏èüíø
      </p>
    </div>
  );
}

export default AlbumList;
