import React, { useRef, useState } from "react";

function Search({ onSearch }) {
  const inputEl = useRef(null);
  const [filter, setFilter] = useState("artists");
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const handleSearch = () => {
    const query = inputEl.current.value;
    onSearch(filter, query);
  };

  const handleFilterChange = (value) => {
    setFilter(value);
    setDropdownOpen(false);
    handleSearch();
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  return (
    <div className="search-container">
      <input
        className="search"
        type="text"
        placeholder="Search..."
        ref={inputEl}
        onChange={handleSearch}
      />
      <div className="dropdown">
        <button onClick={toggleDropdown} className="dropdown-button">
          Filter:{" "}
          {filter === "artists" ? "ðŸŽ¤" : filter === "albums" ? "ðŸ’¿" : "ðŸŽµ"} â–¼
        </button>
        {dropdownOpen && (
          <ul className="dropdown-menu">
            <li onClick={() => handleFilterChange("artists")}>Artists ðŸŽ¤</li>
            <li onClick={() => handleFilterChange("albums")}>Albums ðŸ’¿</li>
            <li onClick={() => handleFilterChange("songs")}>Songs ðŸŽµ</li>
          </ul>
        )}
      </div>
    </div>
  );
}

export default Search;
