import React from "react";

function AddForm({
  handleAdd,
  newEntry,
  setNewEntry,
  title,
  newSongLength,
  setNewSongLength,
}) {
  return (
    <form className="list list-library" onSubmit={(e) => handleAdd(e)}>
      <li>
        <input
          className="edit-text-song"
          type="text"
          value={newEntry}
          onChange={(e) => setNewEntry(e.target.value)}
          placeholder={
            title === "Details ðŸ“ƒ"
              ? "New song... "
              : title === "Albums ðŸ’¿"
              ? "New album.."
              : title === "Artists ðŸŽ¤"
              ? "New artist..."
              : ""
          }
          required
        />
        {title === "Details ðŸ“ƒ" ? (
          <input
            className="edit-text-song-add-length"
            type="text"
            value={newSongLength}
            onChange={(e) => setNewSongLength(e.target.value)}
            placeholder={"0:00"}
            required
          />
        ) : (
          ""
        )}
        <button className="btn-add" type="submit">
          Add
        </button>
      </li>
    </form>
  );
}

export default AddForm;
