import React, { useEffect, useRef, useState } from "react";
import Axios from "axios";

const average = (arr) => arr.reduce((acc, cur) => acc + cur / arr.length, 0);

export default function App() {
  const [list, setList] = useState([]);
  const [selectedAlbum, setSelectedAlbum] = useState(null);

  useEffect(() => {
    Axios.get("http://localhost:4000/api/retrieveMusicLibrary").then((res) =>
      setList(res.data)
    );
  }, []);

  function handleSelectAlbum(album) {
    setSelectedAlbum((selectedAlbum) =>
      album === selectedAlbum ? null : album
    );
  }

  function handleCloseAlbum() {
    setSelectedAlbum(null);
  }

  return (
    <>
      <NavBar>
        <Search />
      </NavBar>

      <Main>
        <Box>
          <MusicList list={list} onSelectAlbum={handleSelectAlbum} />
        </Box>

        <Box>
          {selectedAlbum ? (
            <AlbumDetails
              album={selectedAlbum}
              onCloseAlbum={handleCloseAlbum}
            />
          ) : (
            <p>Select an album to see the details</p>
          )}
        </Box>
      </Main>
    </>
  );
}

function NavBar({ children }) {
  return (
    <nav className="nav-bar">
      <Logo />
      {children}
    </nav>
  );
}

function Logo() {
  return (
    <div className="logo">
      <span role="img">ðŸŽ¶</span>
      <h1>Music Library</h1>
    </div>
  );
}

function Search() {
  const inputEl = useRef(null);

  return (
    <input
      className="search"
      type="text"
      placeholder="Search albums..."
      ref={inputEl}
    />
  );
}

function Main({ children }) {
  return <main className="main">{children}</main>;
}

function Box({ children }) {
  const [isOpen, setIsOpen] = useState(true);

  return (
    <div className="box">
      <button className="btn-toggle" onClick={() => setIsOpen((open) => !open)}>
        {isOpen ? "â€“" : "+"}
      </button>
      {isOpen && children}
    </div>
  );
}

function MusicList({ list, onSelectAlbum }) {
  return (
    <ul className="list list-music">
      {list?.map((artist) => (
        <Artist
          artist={artist}
          key={artist.name}
          onSelectAlbum={onSelectAlbum}
        />
      ))}
    </ul>
  );
}

function Artist({ artist, onSelectAlbum }) {
  return (
    <li>
      <h2>{artist.name}</h2>
      <ul>
        {artist.albums.map((album) => (
          <Album
            album={album}
            key={album.title}
            onSelectAlbum={onSelectAlbum}
          />
        ))}
      </ul>
    </li>
  );
}

function Album({ album, onSelectAlbum }) {
  return (
    <li onClick={() => onSelectAlbum(album)}>
      <h3>{album.title}</h3>
    </li>
  );
}

function AlbumDetails({ album, onCloseAlbum }) {
  return (
    <div className="details">
      <header>
        <button className="btn-back" onClick={onCloseAlbum}>
          &larr;
        </button>
        <div className="details-overview">
          <h2>{album.title}</h2>
          <p>{album.description}</p>
        </div>
      </header>
      <section>
        <ul>
          {album.songs.map((song) => (
            <li key={song.title}>
              <span>{song.title}</span>
              <span>{song.length}</span>
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
}
