import React, { useEffect, useRef, useState } from "react";
import Axios from "axios";

const average = (arr) => arr.reduce((acc, cur) => acc + cur / arr.length, 0);

export default function App() {
  const [list, setList] = useState([]);
  const [selectedArtist, setSelectedArtist] = useState(null);
  const [selectedAlbum, setSelectedAlbum] = useState(null);

  useEffect(() => {
    Axios.get("http://localhost:4000/api/retrieveMusicLibrary").then((res) =>
      setList(res.data)
    );
  }, []);

  function handleSelectArtist(artist) {
    setSelectedArtist((selectedArtist) =>
      artist === selectedArtist ? null : artist
    );
    setSelectedAlbum(null); // Reset selected album when a new artist is selected
  }

  function handleSelectAlbum(album) {
    setSelectedAlbum((selectedAlbum) =>
      album === selectedAlbum ? null : album
    );
  }

  function handleCloseAlbum() {
    setSelectedAlbum(null);
  }

  async function handleDeleteArtist(artistName) {
    await Axios.delete(`http://localhost:4000/api/deleteArtist/${artistName}`);
    setList((list) => list.filter((artist) => artist.name !== artistName));
    setSelectedArtist(null);
    setSelectedAlbum(null);
  }

  async function handleDeleteAlbum(artistName, albumTitle) {
    await Axios.delete(
      `http://localhost:4000/api/deleteAlbum/${artistName}/${albumTitle}`
    );
    setList((list) =>
      list.map((artist) =>
        artist.name === artistName
          ? {
              ...artist,
              albums: artist.albums.filter(
                (album) => album.title !== albumTitle
              ),
            }
          : artist
      )
    );
    setSelectedAlbum(null);
  }

  return (
    <>
      <NavBar>
        <Search />
      </NavBar>

      <Main>
        <Box title="Artists">
          <ArtistList
            list={list}
            onSelectArtist={handleSelectArtist}
            onDeleteArtist={handleDeleteArtist}
          />
        </Box>

        <Box title="Albums">
          {selectedArtist ? (
            <AlbumList
              artist={selectedArtist}
              onSelectAlbum={handleSelectAlbum}
              onDeleteAlbum={handleDeleteAlbum}
            />
          ) : (
            <p>Select an artist to see the albums</p>
          )}
        </Box>

        <Box title="Album Details">
          {selectedAlbum ? (
            <AlbumDetails
              album={selectedAlbum}
              onCloseAlbum={handleCloseAlbum}
            />
          ) : (
            <p>Select an album to see the details</p>
          )}
        </Box>
      </Main>
    </>
  );
}

function NavBar({ children }) {
  return (
    <nav className="nav-bar">
      <Logo />
      {children}
    </nav>
  );
}

function Logo() {
  return (
    <div className="logo">
      <span role="img">ðŸŽ¶</span>
      <h1>Music Library</h1>
    </div>
  );
}

function Search() {
  const inputEl = useRef(null);

  return (
    <input
      className="search"
      type="text"
      placeholder="Search albums..."
      ref={inputEl}
    />
  );
}

function Main({ children }) {
  return <main className="main">{children}</main>;
}

function Box({ title, children }) {
  const [isOpen, setIsOpen] = useState(true);

  return (
    <div className="box">
      <button className="btn-toggle" onClick={() => setIsOpen((open) => !open)}>
        {isOpen ? "â€“" : "+"}
      </button>
      <h2>{title}</h2>
      {isOpen && children}
    </div>
  );
}

function ArtistList({ list, onSelectArtist, onDeleteArtist }) {
  return (
    <ul className="list list-artists">
      {list?.map((artist) => (
        <Artist
          artist={artist}
          key={artist.name}
          onSelectArtist={onSelectArtist}
          onDeleteArtist={onDeleteArtist}
        />
      ))}
    </ul>
  );
}

function Artist({ artist, onSelectArtist, onDeleteArtist }) {
  return (
    <li>
      <h3 onClick={() => onSelectArtist(artist)}>{artist.name}</h3>
      <button onClick={() => onDeleteArtist(artist.name)}>Delete</button>
    </li>
  );
}

function AlbumList({ artist, onSelectAlbum, onDeleteAlbum }) {
  return (
    <ul className="list list-albums">
      {artist.albums.map((album) => (
        <Album
          album={album}
          key={album.title}
          onSelectAlbum={onSelectAlbum}
          onDeleteAlbum={onDeleteAlbum}
          artistName={artist.name}
        />
      ))}
    </ul>
  );
}

function Album({ album, onSelectAlbum, onDeleteAlbum, artistName }) {
  return (
    <li>
      <h4 onClick={() => onSelectAlbum(album)}>{album.title}</h4>
      <button onClick={() => onDeleteAlbum(artistName, album.title)}>
        Delete
      </button>
    </li>
  );
}

function AlbumDetails({ album, onCloseAlbum }) {
  return (
    <div className="details">
      <header>
        <button className="btn-back" onClick={onCloseAlbum}>
          &larr;
        </button>
        <div className="details-overview">
          <h2>{album.title}</h2>
          <p>{album.description}</p>
        </div>
      </header>
      <section>
        <ul>
          {album.songs.map((song) => (
            <li key={song.title}>
              <span>{song.title}</span>
              <span>{song.length}</span>
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
}
