import React, { useState } from "react";
import ActionButtons from "./ActionButtons";

function Song({ artist, album, song, length, onDeleteSong, onUpdate }) {
  const [isEditing, setIsEditing] = useState(false);
  const [newTitle, setNewTitle] = useState(song);
  const [newLength, setNewLength] = useState(length);

  const handleDelete = () => {
    onDeleteSong(artist, album, song.title);
  };

  const handleSave = () => {
    onUpdate(artist, album, song.title, newTitle, newLength);
    setIsEditing(false);
  };

  return isEditing ? (
    <>
      <input
        type="text"
        value={newTitle}
        onChange={(e) => setNewTitle(e.target.value)}
      />
      <input
        type="text"
        value={newLength}
        onChange={(e) => setNewLength(e.target.value)}
      />
      <button onClick={handleSave}>Save</button>
      <button onClick={() => setIsEditing(false)}>Cancel</button>
    </>
  ) : song && song.title && song.length ? (
    <li className="list list-library">
      <span>
        {song.title} ({song.length})
      </span>

      <ActionButtons
        onDelete={handleDelete}
        //onUpdate={() => setIsEditing(true)}
      />
    </li>
  ) : (
    <div className="center-text-container">Invalid song data. ðŸ¥º</div>
  );
}

export default Song;
