import { useEffect, useState, useRef } from "react";
import "./index.css";

export default function App() {
  const [list, setList] = useState([]);
  const [selectedArtist, setSelectedArtist] = useState(null);
  const [selectedAlbum, setSelectedAlbum] = useState(null);
  const [showArtistForm, setShowArtistForm] = useState(false);
  const [showAlbumForm, setShowAlbumForm] = useState(false);
  const [showSongForm, setShowSongForm] = useState(false);

  useEffect(() => {
    fetch("http://localhost:4000/api/retrieveMusicLibrary")
      .then((res) => res.json())
      .then((data) => setList(data));
  }, []);

  function handleSelectArtist(artist) {
    setSelectedArtist(artist);
    setSelectedAlbum(null);
  }

  function handleSelectAlbum(album) {
    setSelectedAlbum(album);
  }

  function handleCloseAlbum() {
    setSelectedAlbum(null);
  }

  function handleDeleteArtist(artistName) {
    fetch(`http://localhost:4000/api/deleteArtist/${artistName}`, {
      method: "DELETE",
    }).then(() => {
      setList((list) => list.filter((artist) => artist.name !== artistName));
      setSelectedArtist(null);
      setSelectedAlbum(null);
    });
  }

  function handleDeleteAlbum(artistName, albumTitle) {
    fetch(
      `http://localhost:4000/api/deleteAlbum/${encodeURIComponent(
        artistName
      )}/${encodeURIComponent(albumTitle)}`,
      {
        method: "DELETE",
      }
    ).then(() => {
      setList((list) =>
        list.map((artist) =>
          artist.name === artistName
            ? {
                ...artist,
                albums: artist.albums.filter(
                  (album) => album.title !== albumTitle
                ),
              }
            : artist
        )
      );
      setSelectedAlbum(null);
    });
  }

  function handleUpdateArtist(oldName, newName) {
    fetch(`http://localhost:4000/api/updateArtist/${oldName}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ oldName, newName }),
    }).then(() => {
      setList((list) =>
        list.map((artist) =>
          artist.name === oldName ? { ...artist, name: newName } : artist
        )
      );
      if (selectedArtist && selectedArtist.name === oldName) {
        setSelectedArtist((prevSelectedArtist) => ({
          ...prevSelectedArtist,
          name: newName,
        }));
      }
    });
  }

  function handleUpdateAlbum(artistName, oldTitle, newTitle, newDescription) {
    fetch(`http://localhost:4000/api/updateAlbum/${artistName}/${oldTitle}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        artistName,
        oldTitle,
        newTitle,
        newDescription,
      }),
    }).then(() => {
      setList((list) =>
        list.map((artist) =>
          artist.name === artistName
            ? {
                ...artist,
                albums: artist.albums.map((album) =>
                  album.title === oldTitle
                    ? { ...album, title: newTitle, description: newDescription }
                    : album
                ),
              }
            : artist
        )
      );

      if (selectedAlbum && selectedAlbum.title === oldTitle) {
        setSelectedAlbum((prevSelectedAlbum) => ({
          ...prevSelectedAlbum,
          title: newTitle,
          description: newDescription,
        }));
      }
    });
  }

  function handleAddArtist(name) {
    fetch("http://localhost:4000/api/addArtist", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ name }),
    }).then(() => {
      setList([...list, { name, albums: [] }]);
      setShowArtistForm(false);
    });
  }

  function handleAddAlbum(artistName, title, description) {
    fetch("http://localhost:4000/api/addAlbum", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ artistName, title, description }),
    }).then(() => {
      setList((list) =>
        list.map((artist) =>
          artist.name === artistName
            ? {
                ...artist,
                albums: [...artist.albums, { title, description, songs: [] }],
              }
            : artist
        )
      );
      setShowAlbumForm(false);
    });
  }

  function handleAddSong(artistName, albumTitle, songTitle, songLength) {
    fetch("http://localhost:4000/api/addSong", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ artistName, albumTitle, songTitle, songLength }),
    }).then(() => {
      setList((list) =>
        list.map((artist) =>
          artist.name === artistName
            ? {
                ...artist,
                albums: artist.albums.map((album) =>
                  album.title === albumTitle
                    ? {
                        ...album,
                        songs: [
                          ...album.songs,
                          { title: songTitle, length: songLength },
                        ],
                      }
                    : album
                ),
              }
            : artist
        )
      );
      setShowSongForm(false);
    });
  }

  return (
    <>
      <NavBar>
        <Search />
      </NavBar>

      <Main>
        <Box title="Artists üé§">
          <button onClick={() => setShowArtistForm((prev) => !prev)}>
            {showArtistForm ? "Close Form" : "Add Artist"}
          </button>
          {showArtistForm && <ArtistForm onAddArtist={handleAddArtist} />}
          <ArtistList
            list={list}
            onSelectArtist={handleSelectArtist}
            onDeleteArtist={handleDeleteArtist}
            onUpdateArtist={handleUpdateArtist}
            selectedArtist={selectedArtist}
          />
        </Box>

        <Box title="Albums üíø">
          {selectedArtist ? (
            <>
              <button onClick={() => setShowAlbumForm((prev) => !prev)}>
                {showAlbumForm ? "Close Form" : "Add Album"}
              </button>
              {showAlbumForm && (
                <AlbumForm
                  artistName={selectedArtist.name}
                  onAddAlbum={handleAddAlbum}
                />
              )}
              <AlbumList
                artist={selectedArtist}
                onSelectAlbum={handleSelectAlbum}
                onDeleteAlbum={handleDeleteAlbum}
                onUpdateAlbum={handleUpdateAlbum}
                selectedAlbum={selectedAlbum}
              />
            </>
          ) : (
            <div className="center-text-container">
              <p>Select an artist first in order to see the albums. üëàüèª‚ö†Ô∏è</p>
            </div>
          )}
        </Box>

        <Box title="Details üìÉ">
          {selectedAlbum ? (
            <>
              <button onClick={() => setShowSongForm((prev) => !prev)}>
                {showSongForm ? "Close Form" : "Add Song"}
              </button>
              {showSongForm && (
                <SongForm
                  artistName={selectedArtist.name}
                  albumTitle={selectedAlbum.title}
                  onAddSong={handleAddSong}
                />
              )}
              <AlbumDetails
                album={selectedAlbum}
                onCloseAlbum={handleCloseAlbum}
              />
            </>
          ) : (
            <div className="center-text-container">
              <p>Select an album first in order to see the details. üëàüèª‚ö†Ô∏è</p>
            </div>
          )}
        </Box>
      </Main>
    </>
  );
}

function NavBar({ children }) {
  return (
    <nav className="nav-bar">
      <Logo />
      {children}
    </nav>
  );
}

function Logo() {
  return (
    <div className="logo">
      <span role="img">üé∂</span>
      <h1>Music Library</h1>
    </div>
  );
}

function Search() {
  const inputEl = useRef(null);

  return (
    <input
      className="search"
      type="text"
      placeholder="Search albums..."
      ref={inputEl}
    />
  );
}

function Main({ children }) {
  return <main className="main">{children}</main>;
}

function Box({ title, children }) {
  return (
    <section className="box">
      <h2>{title}</h2>
      {children}
    </section>
  );
}

function ArtistList({
  list,
  onSelectArtist,
  onDeleteArtist,
  onUpdateArtist,
  selectedArtist,
}) {
  return (
    <ul className="list">
      {list.map((artist) => (
        <Artist
          artist={artist}
          key={artist.name}
          onSelectArtist={onSelectArtist}
          onDeleteArtist={onDeleteArtist}
          onUpdateArtist={onUpdateArtist}
          selectedArtist={selectedArtist}
        />
      ))}
    </ul>
  );
}

function Artist({
  artist,
  onSelectArtist,
  onDeleteArtist,
  onUpdateArtist,
  selectedArtist,
}) {
  const [isEditing, setIsEditing] = useState(false);
  const [newName, setNewName] = useState(artist.name);

  const handleUpdateClick = () => {
    if (isEditing) {
      onUpdateArtist(artist.name, newName);
    }
    setIsEditing(!isEditing);
  };

  return (
    <li className={selectedArtist === artist ? "selected" : ""}>
      {isEditing ? (
        <input
          type="text"
          value={newName}
          onChange={(e) => setNewName(e.target.value)}
        />
      ) : (
        <span onClick={() => onSelectArtist(artist)}>{artist.name}</span>
      )}
      <button onClick={handleUpdateClick}>{isEditing ? "Save" : "‚úèÔ∏è"}</button>
      <button onClick={() => onDeleteArtist(artist.name)}>üóëÔ∏è</button>
    </li>
  );
}

function AlbumList({
  artist,
  onSelectAlbum,
  onDeleteAlbum,
  onUpdateAlbum,
  selectedAlbum,
}) {
  return (
    <ul className="list">
      {artist.albums.map((album) => (
        <Album
          album={album}
          key={album.title}
          onSelectAlbum={onSelectAlbum}
          onDeleteAlbum={onDeleteAlbum}
          onUpdateAlbum={onUpdateAlbum}
          artistName={artist.name}
          selectedAlbum={selectedAlbum}
        />
      ))}
    </ul>
  );
}

function Album({
  album,
  onSelectAlbum,
  onDeleteAlbum,
  onUpdateAlbum,
  artistName,
  selectedAlbum,
}) {
  const [isEditing, setIsEditing] = useState(false);
  const [newTitle, setNewTitle] = useState(album.title);
  const [newDescription, setNewDescription] = useState(album.description);

  const handleUpdateClick = () => {
    if (isEditing) {
      onUpdateAlbum(artistName, album.title, newTitle, newDescription);
    }
    setIsEditing(!isEditing);
  };

  return (
    <li className={selectedAlbum === album ? "selected" : ""}>
      {isEditing ? (
        <>
          <input
            type="text"
            value={newTitle}
            onChange={(e) => setNewTitle(e.target.value)}
          />
          <input
            type="text"
            value={newDescription}
            onChange={(e) => setNewDescription(e.target.value)}
          />
        </>
      ) : (
        <span onClick={() => onSelectAlbum(album)}>
          {album.title}: {album.description}
        </span>
      )}
      <button onClick={handleUpdateClick}>{isEditing ? "Save" : "‚úèÔ∏è"}</button>
      <button onClick={() => onDeleteAlbum(artistName, album.title)}>üóëÔ∏è</button>
    </li>
  );
}

function AlbumDetails({ album, onCloseAlbum }) {
  return (
    <div className="details">
      <h3>{album.title}</h3>
      <p>{album.description}</p>
      <ul>
        {album.songs.map((song, index) => (
          <li key={index}>
            {song.title} - {song.length}
          </li>
        ))}
      </ul>
      <button onClick={onCloseAlbum}>Close</button>
    </div>
  );
}

function ArtistForm({ onAddArtist }) {
  const [name, setName] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    onAddArtist(name);
    setName("");
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Artist Name"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <button type="submit">Add Artist</button>
    </form>
  );
}

function AlbumForm({ artistName, onAddAlbum }) {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    onAddAlbum(artistName, title, description);
    setTitle("");
    setDescription("");
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Album Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
      />
      <input
        type="text"
        placeholder="Album Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <button type="submit">Add Album</button>
    </form>
  );
}

function SongForm({ artistName, albumTitle, onAddSong }) {
  const [songTitle, setSongTitle] = useState("");
  const [songLength, setSongLength] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    onAddSong(artistName, albumTitle, songTitle, songLength);
    setSongTitle("");
    setSongLength("");
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Song Title"
        value={songTitle}
        onChange={(e) => setSongTitle(e.target.value)}
      />
      <input
        type="text"
        placeholder="Song Length"
        value={songLength}
        onChange={(e) => setSongLength(e.target.value)}
      />
      <button type="submit">Add Song</button>
    </form>
  );
}
